//Libraries
import axios from 'axios';

//Styles
import styles from '../../styles/home.module.css'

//Components
import Layout from '../../components/Layout';
import Link from 'next/link';


export default function singlePokemon ({ pokemon }){

    return (
        <Layout>

            <h1>{pokemon.name}</h1>

            <img src = {pokemon.image} alt = "Pokemon Image" />

            <div className = {styles.types}>
                <h2>Type</h2>
                    {
                        pokemon.types.map( (type, index) => (
                            <p className = {type.type.name}  key = {index}>{type.type.name}</p>
                        ))
                    }
            </div>

            <Link href = "/">
                <a className = {styles.homeButton}>Home</a>
            </Link>

        </Layout>
    )
};


export async function getStaticProps () {

    const response = await axios("https://pokeapi.co/api/v2/pokemon?limit=151&offset=0");
    const results = await response.data.results;
    
    const pokemons = results.map( (pokemon, index) => {
        let paddedIndex = index < 9 ? "00": index >= 9 && index < 99 ? "0": "";
        paddedIndex += (index + 1)

        let image = `https://assets.pokemon.com/assets/cms2/img/pokedex/full/${paddedIndex}.png`;

        return {
            ...pokemon,
            image,
            index: (index + 1)
        }
    });

    return { props: { pokemons } }
}


export async function getStaticPaths() {

    return {
        paths: [
            {params: {id: "/pokemon/1"}}
        ],
        fallback: false
    }

}
