//Libraries
import axios from 'axios';

//Styles
import Layout from '../components/Layout';
import styles from '../styles/home.module.css';


export default function Home ({ pokemons }) {

    console.log(pokemons)
    
    return (
        <Layout>
            <h1 className = { styles.title }>Pok√©dex</h1>
        </Layout>
    )
}

export async function getStaticProps () {

    const response = await axios("https://pokeapi.co/api/v2/pokemon?limit=150&offset=0");
    const results = await response.data.results;
    console.log(results)

    const pokemons = results.map( (index, pokemon) => {

        const paddedIndex = "00" + (index + 1);
        console.log(paddedIndex)
        const image = `https://assets.pokemon.com/assets/cms2/img/pokedex/full/${paddedIndex}.png`

        return {
            ...pokemon,
            image 
        }
    });

    return { props: { pokemons } }
}
