//Libraries
import React from 'react';
import Link from 'next/link';

const Pokemon = ({ pokemon, pokemonUrl}) => {

    const { name, image, index } = pokemon;
    let paddedIndex = index < 9 ? "00": index >= 9 && index < 99 ? "0": "";
    console.log(paddedIndex)

    return (
        <li>
            <h3><span>{ paddedIndex  + 1}</span>{ name }</h3>
            
            <Link href = {pokemonUrl}>
                <a><img src = {image} alt = "Pokemon Image" /></a>
            </Link>
            
        </li>
    );
}
 
export default Pokemon;
