//Libraries
import axios from 'axios';


//Components
import Layout from '../../components/Layout';


export default function singlePokemon ({ pokemon }){

    console.log(pokemon)

    return (
        <Layout>
            <h1>Pok√©mon</h1>
        </Layout>
    )
};


export async function getServerSideProps ({ query }) {
    let { id } = query;
    const response = await axios(`https://pokeapi.co/api/v2/pokemon/${id}`);
    const result = await response.data;

    const pokemon = result.map( (pokemon, index) => {
        let paddedIndex = index < 9 ? "00": index >= 9 && index < 99 ? "0": "";
        paddedIndex += (index + 1)

        let image = `https://assets.pokemon.com/assets/cms2/img/pokedex/full/${paddedIndex}.png`;

        return {
            ...pokemon,
            image
        }
    });
    
    return { props: { pokemon }}

}
