//Libraries
import axios from 'axios';
import { useState } from 'react';

//Styles
import Layout from '../components/Layout';
import styles from '../styles/home.module.css';

//Components
import Pokemon from '../components/Pokemon';


export default function Home ({ pokemons }) {

    const [allPokemons, setAllPokemons] = useState(pokemons);
    const [filteredPokemons, setFilterPokemons] = useState([]);


    //Handlers
    const searchFn = (input) => {
        let search = input;
        const filteredPokemons = [];

        if(search === "") {
            return pokemons;
        }

        for (let pokemon of allPokemons) {
            if (pokemon.name.includes(search.toLowerCase())) {
                filteredPokemons.push(pokemon)
            }
        }

        return filteredPokemons;

    }

    const handleChange = e => {
 
        let searchResult = searchFn(e.target.value);
        setFilterPokemons(searchResult)
        
    }

    return (
        <>
            <div className = {styles.header}>
                <h1 className = { styles.title }>Pok√©dex</h1>
                <form>
                    <input
                        className = {styles.search}
                        type = "text"
                        onChange = { handleChange } />
                </form>  
            </div>

            <Layout>
                
                { allPokemons.length === 0 && <h3>No pokemons were found :(</h3>}
                <ul className = {styles.pokemonList}>
                    {
                        filteredPokemons.map( (pokemon, index) => (
                            <Pokemon
                            key = {index}
                            pokemon = {pokemon}
                            pokemonUrl = {`/pokemons/${pokemon.index}`} />
                        ))
                    }
                </ul>

            </Layout>
        </>
    )
}

export async function getStaticProps () {

    const response = await axios("https://pokeapi.co/api/v2/pokemon?limit=300&offset=0");
    const results = await response.data.results;
    
    const pokemons = results.map( (pokemon, index) => {
        let paddedIndex = index < 9 ? "00": index >= 9 && index < 99 ? "0": "";
        paddedIndex += (index + 1)

        let image = `https://assets.pokemon.com/assets/cms2/img/pokedex/full/${paddedIndex}.png`;

        return {
            ...pokemon,
            image,
            index: (index + 1)
        }
    });

    return { props: { pokemons } }
}
