//Libraries
import axios from 'axios';

//Styles
import Layout from '../components/Layout';
import styles from '../styles/home.module.css';


export default function Home ({ pokemons }) {

    
    return (
        <Layout>
            <h1 className = { styles.title }>Pok√©dex</h1>
        </Layout>
    )
}

export async function getStaticProps () {

    const response = await axios("https://pokeapi.co/api/v2/pokemon?limit=10&offset=0");
    const results = await response.data.results;
    
    const pokemons = results.map( (pokemon, index) => {
        let paddedIndex = "00" + (index + 1);
        let image = `https://assets.pokemon.com/assets/cms2/img/pokedex/full/${paddedIndex}.png`;
        console.log(pokemon)

        return {
            ...pokemon,
            image
        }
    });

    return { props: { pokemons } }
}
